// Access Unity's console logs using reflection
var logEntriesType = System.Type.GetType("UnityEditor.LogEntries, UnityEditor");

if (logEntriesType != null)
{
    // Get the Clear method
    var clearMethod = logEntriesType.GetMethod("Clear", System.Reflection.BindingFlags.Public | System.Reflection.BindingFlags.Static);

    if (clearMethod != null)
    {
        // Clear the console
        clearMethod.Invoke(null, null);
        return "Unity console cleared successfully";
    }
    else
    {
        return "Error: Could not find Clear method in LogEntries";
    }
}
else
{
    return "Error: Could not access Unity LogEntries type";
}
