queryTemplate:
// Focus Unity Editor window
try
{
    // Method 1: Focus Scene View which brings Unity to foreground
    var sceneView = UnityEditor.SceneView.lastActiveSceneView;
    if (sceneView != null)
    {
        sceneView.Focus();
        return "Unity Editor focused via Scene View";
    }

    // Method 2: Get and focus any open EditorWindow
    var allWindows = UnityEngine.Resources.FindObjectsOfTypeAll<UnityEditor.EditorWindow>();
    if (allWindows != null && allWindows.Length > 0)
    {
        allWindows[0].Focus();
        return $"Unity Editor focused via {allWindows[0].GetType().Name}";
    }

    // Method 3: Repaint all views which can help bring Unity to foreground
    UnityEditor.EditorApplication.ExecuteMenuItem("Window/General/Scene");
    return "Unity Editor Scene window opened and focused";
}
catch (System.Exception ex)
{
    return $"Error focusing Unity: {ex.Message}";
}
